const gulp=require("gulp"),sass=require("gulp-sass"),browserSync=require("browser-sync").create();function style(){return gulp.src("./build/sass/**/*.sass").pipe(sass()).pipe(gulp.dest("./css")).pipe(browserSync.stream())}function watch(){browserSync.init({server:{baseDir:"./"}}),gulp.watch("./build/sass/**/*.sass",style),gulp.watch("./*.html").on("change",browserSync.reload),gulp.watch("./js/**/*.js").on("change",browserSync.reload)}exports.style=style,exports.watch=watch;